const SequenceTraining = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [sequence, setSequence] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentPoseIndex, setCurrentPoseIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [showInstructions, setShowInstructions] = useState(true);

  // Ëé∑ÂèñÂ∫èÂàóÊï∞ÊçÆ
  useEffect(() => {
    // Ê®°ÊãüAPIË∞ÉÁî®
    setTimeout(() => {
      const mockSequence = {
        id: parseInt(id),
        title: 'ËàíÁºìÊîæÊùæÊµÅ',
        description: 'Ëøô‰∏™Â∫èÂàóÊó®Âú®ÂáèËΩªÂéãÂäõÔºåÊîæÊùæË∫´ÂøÉ„ÄÇ',
        level: 'beginner',
        poses: [
          {
            id: 1,
            name: 'Â±±Âºè',
            sanskritName: 'Tadasana',
            duration: 30,
            description: 'Âü∫Á°ÄÁ´ôÂßøÔºåÂüπÂÖªÁ®≥ÂÆöÊÄßÂíåÂßøÂäøÊÑèËØÜ',
            imageUrl: '/api/placeholder/400/400',
            tips: 'ÂèåËÑöÂπ∂Êã¢ÔºåÂùáÂåÄÂàÜÈÖçÈáçÈáèÔºåËÑäÊü±Âª∂Â±ï'
          },
          {
            id: 2,
            name: 'Á´ôÁ´ãÂâçÂ±àÂºè',
            sanskritName: 'Uttanasana',
            duration: 45,
            description: 'Êãâ‰º∏ËÖøÁ≠ãÂíåËÑäÊü±Ôºå‰øÉËøõË°ÄÊ∂≤Âæ™ÁéØ',
            imageUrl: '/api/placeholder/400/400',
            tips: 'ËÜùÁõñÂèØ‰ª•ÂæÆÂæÆÂºØÊõ≤ÔºåÈáçÁÇπÊîæÂú®ÊãâÈïøËÑäÊü±ËÄå‰∏çÊòØËß¶Á¢∞Âú∞Èù¢'
          },
          {
            id: 3,
            name: '‰∏ãÁä¨Âºè',
            sanskritName: 'Adho Mukha Svanasana',
            duration: 60,
            description: 'ÂÖ®Ë∫´‰º∏Â±ïÔºåËàíÁºìÂéãÂäõ',
            imageUrl: '/api/placeholder/400/400',
            tips: 'ÂèåÊâãÊåâÂéãÂú∞Èù¢ÔºåÂùêÈ™®Âêë‰∏äÊèêÂçáÔºåËÑöË∑üÂ∞ΩÈáèÈù†ËøëÂú∞Èù¢'
          },
          {
            id: 4,
            name: 'ÂÑøÁ´•Âºè',
            sanskritName: 'Balasana',
            duration: 45,
            description: 'Ê∏©ÂíåÁöÑ‰ºëÊÅØÂßøÂäøÔºåÊîæÊùæËÉåÈÉ®',
            imageUrl: '/api/placeholder/400/400',
            tips: '‰øùÊåÅÂëºÂê∏ÂùáÂåÄÔºåÊîæÊùæËÇ©ËÜÄÂíåËÉåÈÉ®'
          },
          {
            id: 5,
            name: 'Áå´ÁâõÂºè',
            sanskritName: 'Marjaryasana/Bitilasana',
            duration: 60,
            description: 'Â¢ûÂä†ËÑäÊü±ÁÅµÊ¥ªÊÄßÔºåËàíÁºìËÉåÈÉ®',
            imageUrl: '/api/placeholder/400/400',
            tips: 'Ë∑üÈöèÂëºÂê∏ÊµÅÂä®ÔºåÂê∏Ê∞îÊó∂Âêë‰∏äÁúãÔºåÂëºÊ∞îÊó∂ÂºìËÉå'
          },
          {
            id: 6,
            name: 'È∏ΩÂ≠êÂºè',
            sanskritName: 'Eka Pada Rajakapotasana',
            duration: 60,
            description: 'ÊâìÂºÄÈ´ãÈÉ®ÔºåÂáèËΩª‰∏ãËÉåÈÉ®Á¥ßÂº†',
            imageUrl: '/api/placeholder/400/400',
            tips: '‰øùÊåÅÈ™®ÁõÜÊ≠£ÂØπÂâçÊñπÔºå‰∏çË¶ÅÂÄæÊñú'
          },
          {
            id: 7,
            name: '‰ª∞ÂçßÊâ≠ËΩ¨',
            sanskritName: 'Jathara Parivartanasana',
            duration: 45,
            description: 'Ê∏©ÂíåÊâ≠ËΩ¨ÔºåËàíÁºìËÉåÈÉ®ÂíåËÖπÈÉ®',
            imageUrl: '/api/placeholder/400/400',
            tips: '‰øùÊåÅËÇ©ËÜÄË¥¥Âú∞ÔºåÂëºÊ∞îÊó∂ËøõÂÖ•Êâ≠ËΩ¨'
          },
          {
            id: 8,
            name: 'Â∞∏Âºè',
            sanskritName: 'Savasana',
            duration: 120,
            description: 'Ê∑±Â∫¶ÊîæÊùæÔºåÊï¥ÂêàÁªÉ‰π†ÊïàÊûú',
            imageUrl: '/api/placeholder/400/400',
            tips: 'ËÆ©Ë∫´‰ΩìÂêÑÈÉ®ÂàÜÂÆåÂÖ®ÊîæÊùæÔºå‰øùÊåÅËßâÁü•ÁöÑÂëºÂê∏'
          }
        ]
      };

      setSequence(mockSequence);
      setTimeLeft(mockSequence.poses[0].duration);
      setLoading(false);
    }, 800);
  }, [id]);

  // ËÆ°Êó∂Âô®ÈÄªËæë
  useEffect(() => {
    let timer;

    if (isPlaying && !isPaused && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
    } else if (timeLeft === 0 && isPlaying) {
      // ÂΩìÂâçÂßøÂäøÂÆåÊàêÔºåËøõÂÖ•‰∏ã‰∏Ä‰∏™
      if (currentPoseIndex < sequence?.poses.length - 1) {
        setCurrentPoseIndex(prev => {
          const nextIndex = prev + 1;
          setTimeLeft(sequence.poses[nextIndex].duration);
          return nextIndex;
        });
      } else {
        // Êï¥‰∏™Â∫èÂàóÂÆåÊàê
        setIsPlaying(false);
        // ËøôÈáåÂèØ‰ª•ÊòæÁ§∫ÂÆåÊàêÁïåÈù¢ÊàñÂÖ∂‰ªñÈÄªËæë
      }
    }

    return () => {
      clearInterval(timer);
    };
  }, [isPlaying, isPaused, timeLeft, currentPoseIndex, sequence]);

  const startTraining = () => {
    setShowInstructions(false);
    setIsPlaying(true);
  };

  const togglePause = () => {
    setIsPaused(prev => !prev);
  };

  const nextPose = () => {
    if (currentPoseIndex < sequence?.poses.length - 1) {
      setCurrentPoseIndex(prev => {
        const nextIndex = prev + 1;
        setTimeLeft(sequence.poses[nextIndex].duration);
        return nextIndex;
      });
    }
  };

  const prevPose = () => {
    if (currentPoseIndex > 0) {
      setCurrentPoseIndex(prev => {
        const prevIndex = prev - 1;
        setTimeLeft(sequence.poses[prevIndex].duration);
        return prevIndex;
      });
    }
  };

  const exitTraining = () => {
    navigate(`/sequence/${id}`);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  if (loading) {
    return (
      <div className="training-container">
        <div className="loading">
          <p>Âä†ËΩΩ‰∏≠...</p>
        </div>
      </div>
    );
  }

  const currentPose = sequence.poses[currentPoseIndex];
  const progress = ((currentPoseIndex) / sequence.poses.length) * 100;

  return (
    <div className="training-container">
      {showInstructions ? (
        <div className="training-instructions">
          <h2>ÂáÜÂ§áÂºÄÂßã {sequence.title}</h2>
          <p className="instruction-desc">{sequence.description}</p>

          <div className="instruction-details">
            <div className="instruction-item">
              <span className="instruction-icon">üïí</span>
              <div className="instruction-text">
                <h3>ÊÄªÊó∂Èïø</h3>
                <p>{sequence.poses.reduce((total, pose) => total + pose.duration, 0) / 60} ÂàÜÈíü</p>
              </div>
            </div>

            <div className="instruction-item">
              <span className="instruction-icon">üßò‚Äç‚ôÄÔ∏è</span>
              <div className="instruction-text">
                <h3>‰ΩìÂºèÊï∞Èáè</h3>
                <p>{sequence.poses.length} ‰∏™‰ΩìÂºè</p>
              </div>
            </div>

            <div className="instruction-item">
              <span className="instruction-icon">üí™</span>
              <div className="instruction-text">
                <h3>ÈöæÂ∫¶Á≠âÁ∫ß</h3>
                <p>{sequence.level === 'beginner' ? 'ÂàùÂ≠¶ËÄÖ' : 
                    sequence.level === 'intermediate' ? '‰∏≠Á∫ß' : 'È´òÁ∫ß'}</p>
              </div>
            </div>
          </div>

          <div className="instruction-tips">
            <h3>ËÆ≠ÁªÉÊèêÁ§∫</h3>
            <ul>
              <li>Á°Æ‰øùÊÇ®ÊúâË∂≥Â§üÁöÑÁ©∫Èó¥ËøõË°åÁªÉ‰π†</li>
              <li>ÂáÜÂ§áÂ•ΩÁëú‰ºΩÂû´ÊàñÂÖ∂‰ªñÊüîËΩØÁöÑË°®Èù¢</li>
              <li>Á©øÁùÄËàíÈÄÇÁöÑË°£Áâ©</li>
              <li>‰øùÊåÅ‰∏ìÊ≥®‰∫éÂëºÂê∏</li>
              <li>Â¶ÇÊûúÊÑüÂà∞‰∏çÈÄÇÔºåÈöèÊó∂ÂèØ‰ª•ÊöÇÂÅúÊàñË∞ÉÊï¥ÂßøÂäø</li>
            </ul>
          </div>

          <div className="instruction-actions">
            <button className="btn btn-primary btn-lg" onClick={startTraining}>
              ÂºÄÂßãÁªÉ‰π†
            </button>
            <button className="btn btn-secondary btn-lg" onClick={exitTraining}>
              ËøîÂõûËØ¶ÊÉÖ
            </button>
          </div>
        </div>
      ) : (
        <div className="training-session">
          <div className="training-header">
            <h2>{sequence.title}</h2>
            <div className="training-progress">
              <div className="progress-bar">
                <div className="progress-fill" style={{ width: `${progress}%` }}></div>
              </div>
              <div className="progress-text">
                {currentPoseIndex + 1} / {sequence.poses.length}
              </div>
            </div>
          </div>

          <div className="pose-container">
            <div className="camera-view">
              {/* ËøôÈáåÂèØ‰ª•Ê∑ªÂä†ÊëÑÂÉèÂ§¥ËßÜÂõæÊàñÂç†‰ΩçÁ¨¶ */}
              <div className="camera-placeholder">ÊëÑÂÉèÂ§¥Âå∫Âüü</div>
              <div className="pose-reference">
                <img src={currentPose.imageUrl} alt={currentPose.name} />
              </div>
            </div>

            <div className="pose-info">
              <div className="pose-header">
                <h3>{currentPose.name}</h3>
                <div className="pose-sanskrit">{currentPose.sanskritName}</div>
              </div>

              <div className="pose-timer">
                <div className="timer-display">{formatTime(timeLeft)}</div>
                <div className="timer-controls">
                  <button 
                    className="timer-btn prev-btn" 
                    onClick={prevPose}
                    disabled={currentPoseIndex === 0}
                  >
                    ‰∏ä‰∏Ä‰∏™
                  </button>
                  <button 
                    className="timer-btn play-btn" 
                    onClick={togglePause}
                  >
                    {isPaused ? 'ÁªßÁª≠' : 'ÊöÇÂÅú'}
                  </button>
                  <button 
                    className="timer-btn next-btn" 
                    onClick={nextPose}
                    disabled={currentPoseIndex === sequence.poses.length - 1}
                  >
                    ‰∏ã‰∏Ä‰∏™
                  </button>
                </div>
              </div>

              <div className="pose-description">
                <p>{currentPose.description}</p>
              </div>

              <div className="pose-feedback">
                <h4>AI ÂèçÈ¶à</h4>
                <div className="feedback-content">
                  <p>‰øùÊåÅÂßøÂäøÁ®≥ÂÆöÔºåÊ≥®ÊÑèÂëºÂê∏„ÄÇ</p>
                  <div className="accuracy-meter">
                    <div className="accuracy-label">ÂßøÂäøÂáÜÁ°ÆÂ∫¶</div>
                    <div className="accuracy-bar">
                      <div className="accuracy-fill" style={{ width: '75%' }}></div>
                    </div>
                    <div className="accuracy-value">75%</div>
                  </div>
                </div>
              </div>

              <div className="pose-tips">
                <h4>ÊèêÁ§∫</h4>
                <p>{currentPose.tips}</p>
              </div>

              <button className="exit-btn" onClick={exitTraining}>
                ÈÄÄÂá∫ÁªÉ‰π†
