import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    sessionCount: 0,
    totalTime: 0,
    favoriteSequence: '',
    streak: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    // 检查用户是否登录
    const storedUser = localStorage.getItem('user');
    if (!storedUser) {
      navigate('/login');
      return;
    }

    // 获取用户数据
    setUser(JSON.parse(storedUser));

    // 模拟获取用户统计数据
    setTimeout(() => {
      setStats({
        sessionCount: 24,
        totalTime: 720, // 以分钟为单位
        favoriteSequence: '晨间活力流',
        streak: 5
      });
      setLoading(false);
    }, 800);
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="profile-page">
        <div className="container">
          <div className="loading">
            <p>加载中...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="profile-avatar">
            {user?.name.charAt(0).toUpperCase()}
          </div>
          <div className="profile-info">
            <h2>{user?.name}</h2>
            <p>瑜伽爱好者</p>
          </div>
          <button onClick={handleLogout} className="btn btn-secondary logout-btn">
            退出登录
          </button>
        </div>

        <div className="stats-overview">
          <div className="stat-card">
            <h3>{stats.sessionCount}</h3>
            <p>总练习次数</p>
          </div>
          <div className="stat-card">
            <h3>{Math.floor(stats.totalTime / 60)}小时 {stats.totalTime % 60}分钟</h3>
            <p>总练习时间</p>
          </div>
          <div className="stat-card">
            <h3>{stats.favoriteSequence}</h3>
            <p>最喜爱课程</p>
          </div>
          <div className="stat-card">
            <h3>{stats.streak}天</h3>
            <p>连续练习</p>
          </div>
        </div>

        <div className="profile-sections">
          <div className="profile-section">
            <h3>我的练习历史</h3>
            <div className="activity-calendar">
              {/* 这里可以是日历视图或活动历史图表 */}
              <p className="placeholder-text">日历视图将显示在这里</p>
            </div>
          </div>

          <div className="profile-section">
            <h3>我的最近课程</h3>
            <div className="recent-sequences">
              <div className="sequence-item">
                <div className="sequence-icon">
                  <i className="fa fa-sun"></i>
                </div>
                <div className="sequence-details">
                  <h4>晨间活力流</h4>
                  <p>昨天完成 · 20分钟</p>
                </div>
                <button className="btn btn-small">重新练习</button>
              </div>

              <div className="sequence-item">
                <div className="sequence-icon">
                  <i className="fa fa-moon"></i>
                </div>
                <div className="sequence-details">
                  <h4>睡前放松流</h4>
                  <p>3天前完成 · 15分钟</p>
                </div>
                <button className="btn btn-small">重新练习</button>
              </div>

              <div className="sequence-item">
                <div className="sequence-icon">
                  <i className="fa fa-fire"></i>
                </div>
                <div className="sequence-details">
                  <h4>核心力量训练</h4>
                  <p>上周完成 · 30分钟</p>
                </div>
                <button className="btn btn-small">重新练习</button>
              </div>
            </div>
          </div>

          <div className="profile-section">
            <h3>我的收藏</h3>
            <div className="favorite-poses">
              <div className="pose-item">
                <img src="/api/placeholder/120/120" alt="Warrior II Pose" />
                <p>战士二式</p>
              </div>
              <div className="pose-item">
                <img src="/api/placeholder/120/120" alt="Tree Pose" />
                <p>树式</p>
              </div>
              <div className="pose-item">
                <img src="/api/placeholder/120/120" alt="Downward Dog" />
                <p>下犬式</p>
              </div>
              <div className="pose-item">
                <img src="/api/placeholder/120/120" alt="Bridge Pose" />
                <p>桥式</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
